select * from cpu where cpu.cores * cpu.frequency > (select mult from (with summary as (select cpu.cores * cpu.frequency as mult from game join system_requirements using (id_system_requirements) join minimal_requirements using (id_minimal_requirements) join cpu using (id_cpu)) select mult, ROW_NUMBER () OVER (order by mult) from summary) x where ROW_NUMBER = (select count(*) from game) / 2 + 1);
